python ./scripts/mask_alignment_using_vcf.py -i data/subsampled_alignment_a1_n7149_clockfiltered.fasta -o data/subsampled_alignment.mask.fasta -v data/problematic_sites_sarsCov2.vcf -r Wuhan/Hu-1/2019
